/**
 * Support.
 */
var should = require('chai').should();
var jack = require('jack'); // TODO: use me.

/**
 * The tested object.
 */
var slnks = require('../lib/slnks');

describe('slnks', function() {
  describe('.get()', function() {
    it('should return error if an error occured.', function(done) {
      slnks.get('', function(err, link) {
        err.should.be.an.instanceof(Error);
        done();
      });
    });
    
    it('should return short link', function(done) {
      slnks.get('http://example.com', function(err, link) {
        link.should.match(/^http:\/\/slnks\.com\//);
        done();
      });
    });
  });
});